package org.getalp.blexisma.external.sygfranwrapper.tools;

public class EquivChaine 
{
	public static String equivIso(String chaine)
	{
		String retour = chaine;
		
		if (chaine.equals("")) retour = "";
		else if (chaine.equals("V")) retour = "verb";
		else if (chaine.equals("N")) retour = "noun";
		else if (chaine.equals("PREP")) retour = "preposition";
		else if (chaine.equals("INTERJ")) retour = "interjection";
		else if (chaine.equals("VB")) retour = "conjugatedVerb";
		else if (chaine.equals("INFI")) retour = "infinitiveVerb";
		else if (chaine.equals("NCOM")) retour = "commonNoun";
		else if (chaine.equals("NPRO")) retour = "properNoun";
		else if (chaine.equals("ADVERB")) retour = "adverb";
		else if (chaine.equals("ADNOM")) retour = "qualifierAdjective";
		else if (chaine.equals("ORD")) retour = "ordinalAdjective";
		else if (chaine.equals("ARTD")) retour = "definiteArticle";
		else if (chaine.equals("DEM")) retour = "demonstrativeDeterminer";
		else if (chaine.equals("EXCL")) retour = "exclamativeDeterminer";
		else if (chaine.equals("INDF")) retour = "indefiniteDeterminer";
		else if (chaine.equals("PROP")) retour = "personnalPronoun";
		else if (chaine.equals("POSS")) retour = "possessivePronoun";
		else if (chaine.equals("REL")) retour = "relativePronoun";
		else if (chaine.equals("COORD")) retour = "coordinatingConjunction";
		else if (chaine.equals("SUB")) retour = "subordinatingConjunction";
		else if (chaine.equals("GN")) retour = "nounPhrase";
		else if (chaine.equals("GA")) retour = "adjectivalPhrase";
		else retour = chaine;
		
		return retour;
	}
}

